import React from 'react'
import { Tabs, Table, Input, Button} from 'antd' 
import {getTopRatingMovies, getSearchMovies,getTopReviewMovies} from '../fetcher'
import {searchMovie} from './SearchMovie'

const { TabPane } = Tabs;

const topTenColumns = [
    {
        title: 'Title',
        dataIndex: 'TITLE',
        key: 'TITLE'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Year',
        dataIndex: 'YEAR',
        key: 'YEAR'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Rating',
        dataIndex: 'rating',
        key: 'rating'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Runtime',
        dataIndex: 'runtime',
        key: 'runtime'
        // render: text => <a>{text}</a>
    },
]

const topTenReviewColumns = [
    {
        title: 'Title',
        dataIndex: 'TITLE',
        key: 'TITLE'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Year',
        dataIndex: 'year',
        key: 'YEAR'
        // render: text => <a>{text}</a>
    },
    {
        title: 'RatingCounts',
        dataIndex: 'RatingCounts',
        key: 'RatingCounts'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Runtime',
        dataIndex: 'runtime',
        key: 'runtime'
        // render: text => <a>{text}</a>
    },
]



const SearchColumns = [
    {
        title: 'Movie Name',
        dataIndex: 'name',
        key: 'name'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Realease Date',
        dataIndex: 'rel_date',
        key: 'rel_date'
        // render: text => <a>{text}</a>
    },
    {
        title: 'director',
        dataIndex: 'director',
        key: 'director'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Runtime',
        dataIndex: 'runtime',
        key: 'runtime'
        // render: text => <a>{text}</a>
    },
    {
        title:'genre',
        dataIndex:'genre',
        key: 'genre'
    },    
    {
        title:'Country',
        dataIndex:'country',
        key: 'country'
    }
]

class TopTen extends React.Component {
    state = {
        data: []
    }

    componentDidMount() {
        this.getTopTenMoveis()
    }

    getTopTenMoveis =  async () => {
        try {
            const result = await getTopRatingMovies(10)
            console.log('result: ', result.results)
            this.setState({
                data: result.results
            })
            console.log('state data: ', this.state.data)
        } catch (e) {
            console.log('error in fetching top movie: ', e)
        }
    }

    render() {
        return (
            <Table columns={topTenColumns} dataSource={this.state.data} />
        )
    }
}

class TopTenReview extends React.Component {
    state = {
        data: []
    }

    componentDidMount() {
        this.getTopTenReviewMoveis()
    }

    getTopTenReviewMoveis =  async () => {
        try {
            const result = await getTopReviewMovies(10)
            console.log('result: ', result.results)
            this.setState({
                data: result.results
            })
            console.log('state data: ', this.state.data)
        } catch (e) {
            console.log('error in fetching top movie: ', e)
        }
    }

    render() {
        return (
            <Table columns={topTenReviewColumns} dataSource={this.state.data} />
        )
    }
}

class UserMainPage extends React.Component {
    render () {
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="Search Movie" key="1">
                    <SearchMovie />
                </TabPane>
                <TabPane tab="My Movie" key="2">
                    <div>My Movie Main Page</div>
                </TabPane>
                <TabPane tab="Top 10 Rating Movie" key="3">
                    <TopTen />
                </TabPane>
                <TabPane tab="Top 10 Review Movie" key="4">
                    <TopTenReview />
                </TabPane>
          </Tabs>
        )
    }
}

export default UserMainPage
