import React from 'react'
import { Tabs, Table, Input} from 'antd' 
import {getTopRatingMovies} from '../fetcher'

const { TabPane } = Tabs;

const topTenColumns = [
    {
        title: 'Title',
        dataIndex: 'TITLE',
        key: 'TITLE'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Year',
        dataIndex: 'YEAR',
        key: 'YEAR'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Rating',
        dataIndex: 'rating',
        key: 'rating'
        // render: text => <a>{text}</a>
    },
    {
        title: 'Runtime',
        dataIndex: 'runtime',
        key: 'runtime'
        // render: text => <a>{text}</a>
    },
]

class SearchMovie extends React.Component{
    state = {
        data: [],
        keyword: '',
        genre: '',
        year: '',
        actor: '',
        country: ''
    }

    keywordHandleOnChange = (value) => {
        this.setState({
            keyword: value
        })
        console.log('keyword: ', this.state.keyword)
    }
    render() {
        return (
            <div class="seach_movie flex">
                <div class="w-1/3">
                    <div class="flex">
                        <div class="w-1/4">Keyword</div>
                        <div>
                            <Input placeholder='movie keyword' onChange={this.keywordHandleOnChange}/>
                        </div>
                    </div>
                    <div class="flex mt-2">
                        <div class="w-1/4">Genre</div>
                        <div>
                            <Input placeholder='movie genre'/>
                        </div>
                    </div>
                    <div class="flex mt-2">
                        <div class="w-1/4">Actor</div>
                        <div>
                            <Input placeholder='movie actor'/>
                        </div>
                    </div>
                    <div class="flex mt-2">
                        <div class="w-1/4">Year</div>
                        <div>
                            <Input placeholder='movie year'/>
                        </div>
                    </div>
                    <div class="flex mt-2">
                        <div class="w-1/4">Country</div>
                        <div>
                            <Input placeholder='movie country'/>
                        </div>
                    </div>
                </div>
                <div class="w-2/3">
                    table area
                </div>
            </div>
        )
    }
}
class TopTen extends React.Component {
    state = {
        data: []
    }

    componentDidMount() {
        this.getTopTenMoveis()
    }

    getTopTenMoveis =  async () => {
        try {
            const result = await getTopRatingMovies(10)
            console.log('result: ', result.results)
            this.setState({
                data: result.results
            })
            console.log('state data: ', this.state.data)
        } catch (e) {
            console.log('error in fetching top movie: ', e)
        }
    }

    render() {
        return (
            <Table columns={topTenColumns} dataSource={this.state.data} />
        )
    }
}

class UserMainPage extends React.Component {
    render () {
        return (
            <Tabs defaultActiveKey="1" destroyInactiveTabPane={true}>
                <TabPane tab="Search Movie" key="1">
                    <SearchMovie />
                </TabPane>
                <TabPane tab="My Movie" key="2">
                    <div>My Movie Main Page</div>
                </TabPane>
                <TabPane tab="Top 10 Movie" key="3">
                    <TopTen />
                </TabPane>
          </Tabs>
        )
    }
}

export default UserMainPage
